import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const MovieDetails = () => {
  const { id } = useParams(); // Obtém o ID do filme da URL
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      const options = {
        method: "GET",
        headers: {
          accept: "application/json",
          Authorization: "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIxNjk1ZTUyZmUzZWJmZTY0ZWIyNjA5N2VkMWFmZDA3OCIsIm5iZiI6MTc0NTc4NTg1MC44NDksInN1YiI6IjY4MGU5M2ZhNWE5Yjc4ZDI3MjgxMjI1MCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.xMnrq7OMc9fqXmL0HgEqgO0laVzDHImj4g_ZVGZq76o",
        },
      };

      try {
        const response = await fetch(
          `https://api.themoviedb.org/3/movie/${id}?language=en-US`,
          options
        );
        const data = await response.json();
        setMovie(data);
        setLoading(false);
      } catch (error) {
        console.error("Erro ao buscar os detalhes do filme:", error);
        setLoading(false);
      }
    };

    fetchMovieDetails();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!movie) {
    return <div>Detalhes do filme não encontrados.</div>;
  }

  return (
    <div className="movie-details">
      <div
        className="banner"
        style={{
          backgroundImage: `url(https://image.tmdb.org/t/p/original${movie.backdrop_path})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "60vh",
        }}
      >
        <h1 className="movie-title">{movie.title}</h1>
      </div>
      <div className="container">
        <h2>Overview</h2>
        <p>{movie.overview}</p>
        <p>
          <strong>Release Date:</strong> {movie.release_date}
        </p>
        <p>
          <strong>Rating:</strong> {movie.vote_average} / 10
        </p>
      </div>
    </div>
  );
};

export default MovieDetails;
